import React from "react";
import TooltipTrigger from "react-popper-tooltip";
import "react-popper-tooltip/dist/styles.css";

const modifiers = [
  {
    name: "offset",
    enabled: true,
    options: {
      offset: [0, 4],
    },
  },
];

function Trigger({ getTriggerProps, triggerRef }) {
  return (
    <span
      {...getTriggerProps({
        ref: triggerRef,
        className: "trigger",
      })}
    >
      Click Me!
    </span>
  );
}

function Tooltip({
  getTooltipProps,
  getArrowProps,
  tooltipRef,
  arrowRef,
  placement,
}) {
  return (
    <div
      {...getTooltipProps({
        ref: tooltipRef,
        className: "tooltip-container",
      })}
    >
      <div
        {...getArrowProps({
          ref: arrowRef,
          "data-placement": placement,
          className: "tooltip-arrow",
        })}
      />
      <div className="tooltip-body">Hello, World!</div>
    </div>
  );
}

function Example() {
  return (
    <TooltipTrigger
      placement="right"
      trigger="click"
      tooltip={Tooltip}
      modifiers={modifiers}
    >
      {Trigger}
    </TooltipTrigger>
  );
}

export default Example;
